value.value:
paths -> {
  shortendPaths = $paths.map(path -> {
    shortendPath = $path.reduce(list(), subresult -> element -> {
      if (!$subresult.isEmpty() || $element.instanceOf(`DemoTypes:A`), $subresult.concat($element), $subresult);
    });
    list($path.elementAt(0)).concat($shortendPath);
  });
  $paths.concat($shortendPaths).toSet();
}
